import axios from 'axios'
import React, { Fragment, useEffect, useState ,useContext} from 'react'
import { AiFillPlayCircle } from 'react-icons/ai'
import { AiOutlineClose } from 'react-icons/ai'
import no_image from './no-image-available-icon-flat-vector-no-image-available-icon-flat-vector-illustration-132482953.jpg'
import '../Styles/Videos.css'
import { Container } from './NavBar'
function Tv_shows() {
  const [trailer,setTrailer]=useState(false)
  const {toggle,inputValue}=useContext(Container)
   const input=inputValue
  const [showData, setShowsData] = useState([])
  const Shown =input ? 'search':'discover'
  const Images = "https://image.tmdb.org/t/p/w500"
  const Api = `https://api.themoviedb.org/3/${Shown}/tv`
  const TvShows = async () => {
    const data = await axios.get(Api, {
      params: {
        api_key: '97e50f91f39a8298c4d9603b9462060d',
        query:input
      }
    })
    const results = data.data.results
    setShowsData(results)
  }
  useEffect(() => {
    TvShows()
  },[input])
  return (
   
    <Fragment>
   
      <div className={toggle? "mainBgColor" : "secondaryBgColor"}>
        <div className='movies-container'>
      {showData.map((Show) => {
        return (
          <Fragment>
             <div id={trailer ? 'container':"NoContainer"}>
              <AiFillPlayCircle color='#ee0077' fontSize={40} id={ trailer? 'playIcon':"hide"} />
              <img src={Show.poster_path ? `${Images}${Show.poster_path}`: no_image} alt='' />
              <h3 id={Show.name.length>28 ? "smaller-Text" :""}
              className={toggle? "DarkTheme":"LightThemeClose"}>{Show.name}</h3>
            </div>
          </Fragment>
          )
          })}
          <AiOutlineClose onClick={()=>setTrailer(true)}/>
          </div>
          </div>
    </Fragment>
  )
}

export default Tv_shows
