import React, { Fragment } from 'react'
import { useState ,useEffect } from 'react';
import ReactPlayer from 'react-player';
import movieTrailer from 'movie-trailer';
import '../Styles/TrailerMovi.css'
function TrailerMovies ({moviesTitle}) {
const [video, setVideo] = useState("");
const [videoURL, setVideoURL] =useState("https://www.youtube.com/watch?v=_A-O22Eezac&t=9903s");

function handleSearch() {
  setVideo(moviesTitle)
    movieTrailer(video).then((res) => {
    setVideoURL(res);
    });
  }
useEffect(() => {
    handleSearch()
  },[videoURL])
  return (
    
    <Fragment>
      <label>
            Search for any movies/shows:{ " " }
        </label>
        <input type="text" onChange=
            {(e) => { setVideo(e.target.value) }} />
  
        <button onClick={()=>{handleSearch()}}>
            Search
        </button>
        <div className='Container'></div>
        <div className='player'>
    <ReactPlayer url={videoURL} controls={true}/>
    </div>
    </Fragment>

  )
}

export default TrailerMovies
