import React, { Fragment } from 'react'
import { useState ,useEffect } from 'react';
import ReactPlayer from 'react-player';
import movieTrailer from "movie-trailer";
import '../Styles/TrailerMovi.css'

function TrailerTvShows ({TvShowTitle,toggle}) {
const [video, setVideo] = useState("");
const [videoURL, setVideoURL] =useState("https://www.youtube.com/watch?v=_A-O22Eezac&t=9903s");

function handleSearch() {
  setVideo(TvShowTitle)
    movieTrailer(video).then((res) => {
    setVideoURL(res);
    });
  }
 
  useEffect(() =>{handleSearch()},[videoURL])
  return (
    
    <Fragment>
      
        <div className='Container'></div>
        <div className='player'>
        <h1 id={toggle ? "TrailerMovie-name-dark":"TrailerMovie-name-light"}>{TvShowTitle}</h1>
   
    <ReactPlayer url={videoURL} controls={true} width="700px" height={"400px"} muted={false} playIcon={true}/>
    </div>
    </Fragment>

  )
}

export default TrailerTvShows
